# SELECT YEAR(SALES_DATE) AS YEAR, MONTH(SALES_DATE) AS MONTH, GENDER, COUNT(*) AS USERS FROM USER_INFO AS user, ONLINE_SALE AS sale WHERE user.USER_ID = sale.USER_ID GROUP BY YEAR(SALES_DATE), MONTH(SALES_DATE), GENDER HAVING GENDER IS NOT NULL ORDER BY YEAR ASC, MONTH ASC, GENDER ASC;
SELECT YEAR(SALES_DATE) AS YEAR, MONTH(SALES_DATE) AS MONTH, GENDER, COUNT(DISTINCT(sale.USER_ID)) AS USERS FROM USER_INFO AS user, ONLINE_SALE AS sale WHERE user.USER_ID = sale.USER_ID GROUP BY YEAR(SALES_DATE), MONTH(SALES_DATE), GENDER HAVING GENDER IS NOT NULL ORDER BY YEAR ASC, MONTH ASC, GENDER ASC;